const Joi = require('joi');

const customerSchema = Joi.object({

  customername: Joi.string().min(3).max(40).required(),
  businessname: Joi.string().min(3).max(40).required(),
  personalemail: Joi.string().email().required(),
  companyemail: Joi.string().email().required(),
  district: Joi.string().max(20).required(),
  state: Joi.string().max(15).required(),
  phonenumber: Joi.string().pattern(/^\d{10}$/).required(), 
  industrytype: Joi.string().required(),
});

module.exports = {
  validateCustomer: (req, res, next) => {
    const { error } = customerSchema.validate(req.body);
    if (error) {
      return res.status(400).json({ error: error.details[0].message });
    }
    next();
  },
};
